mapper.update:
UPDATE user
SET pwd = #{pwd}
WHERE id = #{id};

mapper.select:
SELECT pwd
FROM user
WHERE id = #{id}

mapper.update:
UPDATE user
SET dpt   = #{dpt},
    phone = #{phone}
WHERE id = #{id};

UPDATE user
SET enabled = #{enabled}
WHERE id = #{id};

mapper.select:
SELECT count(*)
FROM user
WHERE id = #{id}
  AND email = #{email}

